g.script:
      const g = document.currentScript.parentNode;
      for (let x=-200; x<2100; x+=60){
        const y1 = 60 + Math.sin(x/80)*25;
        const y2 = 60 - Math.sin(x/80)*25;
        const l1 = document.createElementNS("http://www.w3.org/2000/svg","line");
        l1.setAttribute("x1",x); l1.setAttribute("y1",y1);
        l1.setAttribute("x2",x); l1.setAttribute("y2",y2);
        g.appendChild(l1);

        const y3 = 120 + Math.sin(x/80)*25;
        const y4 = 120 - Math.sin(x/80)*25;
        const l2 = document.createElementNS("http://www.w3.org/2000/svg","line");
        l2.setAttribute("x1",x); l2.setAttribute("y1",y3);
        l2.setAttribute("x2",x); l2.setAttribute("y2",y4);
        g.appendChild(l2);

        const y5 = 180 + Math.sin(x/80)*25;
        const y6 = 180 - Math.sin(x/80)*25;
        const l3 = document.createElementNS("http://www.w3.org/2000/svg","line");
        l3.setAttribute("x1",x); l3.setAttribute("y1",y5);
        l3.setAttribute("x2",x); l3.setAttribute("y2",y6);
        g.appendChild(l3);
      }
